"use client";

import { useState } from "react";
import { cn } from "@/lib/utils";
import { Mail } from "lucide-react";

interface IconInputProps {
  icon?: React.ReactNode;
  placeholder?: string;
}

export default function Input_02({
  icon = <Mail className="w-5 h-5 text-zinc-400" />,
  placeholder = "Email",
}: IconInputProps) {
  const [value, setValue] = useState("");

  return (
    <div className="w-full max-w-xs">
      <div
        className={cn(
          "flex items-center gap-2",
          "px-3 py-2.5",
          "rounded-lg",
          "bg-zinc-100 dark:bg-zinc-900",
          "border border-transparent",
          "focus-within:bg-white dark:focus-within:bg-zinc-950",
          "focus-within:border-zinc-200 dark:focus-within:border-zinc-800",
          "transition-all duration-300"
        )}
      >
        {icon}
        <input
          type="email"
          value={value}
          onChange={(e) => setValue(e.target.value)}
          placeholder={placeholder}
          className={cn(
            "w-full bg-transparent",
            "text-sm text-zinc-900 dark:text-zinc-100",
            "placeholder:text-zinc-500",
            "focus:outline-none"
          )}
        />
      </div>
    </div>
  );
}

import { useState } from "react";

interface Tag {
    id: string;
    label: string;
    color?: string;
}

interface UseTagsProps {
    onChange?: (tags: Tag[]) => void;
    defaultTags?: Tag[];
    maxTags?: number;
    defaultColors?: string[];
}

export function useTags({
    onChange,
    defaultTags = [],
    maxTags = 10,
    defaultColors = [
        "bg-blue-100 text-blue-700 dark:bg-blue-900/30 dark:text-blue-300",
        "bg-purple-100 text-purple-700 dark:bg-purple-900/30 dark:text-purple-300",
        "bg-green-100 text-green-700 dark:bg-green-900/30 dark:text-green-300",
        "bg-yellow-100 text-yellow-700 dark:bg-yellow-900/30 dark:text-yellow-300",
        "bg-red-100 text-red-700 dark:bg-red-900/30 dark:text-red-300",
    ],
}: UseTagsProps = {}) {
    const [tags, setTags] = useState<Tag[]>(defaultTags);

    function addTag(tag: Tag) {
        if (tags.length >= maxTags) return;

        const newTags = [
            ...tags,
            {
                ...tag,
                color:
                    tag.color ||
                    defaultColors[tags.length % defaultColors.length],
            },
        ];
        setTags(newTags);
        onChange?.(newTags);
        return newTags;
    }

    function removeTag(tagId: string) {
        const newTags = tags.filter((t) => t.id !== tagId);
        setTags(newTags);
        onChange?.(newTags);
        return newTags;
    }

    function removeLastTag() {
        if (tags.length === 0) return;
        return removeTag(tags[tags.length - 1].id);
    }

    return {
        tags,
        setTags,
        addTag,
        removeTag,
        removeLastTag,
        hasReachedMax: tags.length >= maxTags,
    };
}

import { useEffect, type RefObject } from "react";

export function useClickOutside(
    ref: RefObject<HTMLElement>,
    handler: () => void
) {
    useEffect(() => {
        const handleClickOutside = (event: MouseEvent) => {
            if (ref.current && !ref.current.contains(event.target as Node)) {
                handler();
            }
        };

        document.addEventListener("mousedown", handleClickOutside);
        return () =>
            document.removeEventListener("mousedown", handleClickOutside);
    }, [ref, handler]);
}
